const masterVars = $('Initialize Master Variables').first().json;
const auditSummary = $input.first().json;

const endTime = new Date();
const startTime = new Date(masterVars.master_start_time);
const totalDuration = Math.round((endTime - startTime) / 1000);

return {
  status: '‚úÖ MIGRATION COMPLETE',
  master_batch_id: masterVars.master_batch_id,
  execution_id: masterVars.workflow_id,
  duration_seconds: totalDuration,
  duration_human: `${Math.floor(totalDuration / 60)}m ${totalDuration % 60}s`,
  total_tables: auditSummary.total_tables || 0,
  tables_completed: auditSummary.tables_completed || 0,
  tables_failed: auditSummary.tables_failed || 0,
  total_records_migrated: auditSummary.total_records_migrated || 0,
  total_errors: auditSummary.total_errors || 0,
  completed_at: endTime.toISOString()
};

const report = $input.first().json;
const errorRate = report.total_records_migrated > 0 ? 
  (report.total_errors / report.total_records_migrated) * 100 : 0;

// Build notification message
const notification = {
  title: 'Migration Summary',
  status: report.status,
  master_batch_id: report.master_batch_id,
  execution_id: report.execution_id,
  duration: report.duration_human,
  summary: {
    total_tables: report.total_tables,
    tables_completed: report.tables_completed,
    tables_failed: report.tables_failed,
    total_records_migrated: report.total_records_migrated,
    total_errors: report.total_errors,
    error_rate_percent: Math.round(errorRate * 100) / 100
  },
  completed_at: report.completed_at,
  alert_level: errorRate > 1 ? 'WARNING' : report.tables_failed > 0 ? 'INFO' : 'SUCCESS',
  message: `Migration completed: ${report.tables_completed}/${report.total_tables} tables, ${report.total_records_migrated} records migrated, ${report.total_errors} errors (${Math.round(errorRate * 100) / 100}% error rate) in ${report.duration_human}`
};

// Log to console for visibility
console.log('üìä Migration Notification:', JSON.stringify(notification, null, 2));

return notification;

// This node can be configured to send webhook to Slack, email, etc.
// For now, it logs the notification
const notification = $input.first().json;

console.log('üö® ALERT: High error rate detected!');
console.log('Error Rate:', notification.summary.error_rate_percent + '%');
console.log('Total Errors:', notification.summary.total_errors);

// Return notification for potential webhook integration
return {
  webhook_url: 'https://hooks.slack.com/services/YOUR/WEBHOOK/URL', // Configure as needed
  payload: {
    text: `üö® Migration Alert: ${notification.message}`,
    attachments: [{
      color: 'warning',
      fields: [
        { title: 'Error Rate', value: notification.summary.error_rate_percent + '%', short: true },
        { title: 'Total Errors', value: notification.summary.total_errors.toString(), short: true },
        { title: 'Tables Failed', value: notification.summary.tables_failed.toString(), short: true },
        { title: 'Master Batch ID', value: notification.master_batch_id, short: true }
      ]
    }]
  },
  notification: notification
};

